import { Component, OnInit } from '@angular/core';
import { FormArray, FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { Observable } from 'rxjs';

@Component({
  selector: 'app-reactive',
  templateUrl: './reactive.component.html',
  styles: [`
    input.ng-touched.ng-invalid {
      border: 1px solid red;
    }
  `]
})
export class ReactiveComponent implements OnInit {
  myForm: FormGroup;
  genders = ["male", "female"];

  constructor(private formBuilder: FormBuilder) { }

  ngOnInit(): void {
    // this.myForm = new FormGroup({
    //   "userData": new FormGroup({
    //     "username": new FormControl(null, Validators.required),
    //     "email": new FormControl(null, [
    //       Validators.required,
    //       Validators.pattern("[a-z0-9!#$%&'*+/=?^_`{|}~-]+(?:\.[a-z0-9!#$%&'*+/=?^_`{|}~-]+)*@(?:[a-z0-9](?:[a-z0-9-]*[a-z0-9])?\.)+[a-z0-9](?:[a-z0-9-]*[a-z0-9])?")])
    //     }),      
    //   "password": new FormControl(null, Validators.required),
    //   "gender": new FormControl("male"),
    //   "hobbies": new FormArray([
    //     new FormControl("Cooking", Validators.required)
    //   ])
    // });

    this.myForm = this.formBuilder.group({
      "userData": this.formBuilder.group({
        "username": this.formBuilder.control(null, [
          Validators.required, this.exampleValidator
        ]),
        "email": this.formBuilder.control(null, [
          Validators.required, Validators.pattern("[a-z0-9!#$%&'*+/=?^_`{|}~-]+(?:\.[a-z0-9!#$%&'*+/=?^_`{|}~-]+)*@(?:[a-z0-9](?:[a-z0-9-]*[a-z0-9])?\.)+[a-z0-9](?:[a-z0-9-]*[a-z0-9])?")
        ])
      }),
      "password": [null, Validators.required],
      "gender": ["male"],
      "hobbies": this.formBuilder.array([
        [null, Validators.required]
      ])
    })
  }

  exampleValidator(control: FormControl): {[s: string]: boolean} {
    if(control.value == "example"){
      return {example: true};
    }
    return null;
  }

  asyncExampleValidator(control: FormControl): Promise<any> | Observable<any> {

  }

  onSubmit(){
    console.log(this.myForm);
  }

  get hobbiesFormGroups(){
    return this.myForm.get("hobbies") as FormArray;
  }

  onAddHobby(value: string){
    this.hobbiesFormGroups.push(new FormControl(value, Validators.required));
  }

}
<h1>Reactive</h1>
<form [formGroup]="myForm" (ngSubmit)="onSubmit()">
  <div formGroupName="userData">
    <div class="form-group">
      <label for="username">Username</label>
      <input type="text"
             class="form-control"
             id="username"
             formControlName="username"
      >
    </div>
    <div class="form-group">
      <label for="email">E-Mail</label>
      <input type="text"
             class="form-control"
             id="email"
             formControlName="email"
      >
      <div *ngIf="!myForm.get(['userData', 'email'])?.valid">Ungültige E-Mail-Adresse</div>
    </div>
  </div>
  <div class="form-group">
    <label for="password">Password</label>
    <input type="password"
           class="form-control"
           id="password"
           formControlName="password"
    >
  </div>
  <div class="radio" *ngFor="let g of genders">
    <label>
      <input type="radio"
      formControlName="gender"
      [value]="g"
      >{{ g }}
    </label>
  </div>
  <div formArrayName="hobbies">
    <h3>Hobbys</h3>
    <div class="form-group" *ngFor="let hobby of hobbiesFormGroups.controls; let i = index">
      <input type="text" class="form-control" formControlName="{{ i }}">
    </div>
  </div>
  <button type="button" class="btn btn-default" (click)="onAddHobby(hobby.value)" #hobby>Neues Hobby</button>
  <button type="submit" class="btn btn-primary" [disabled]="!myForm.valid">Submit</button>
</form>
